mapper.insert:
insert
  into freenote
  (
     freenote_no
   , freenote_title
   , freenote_content
   , freenote_category
   , freenote_topic
   , freenote_privacy
   , freenote_privacy_comm
   , freenote_comment_yn
   , member_no
  )
  values
  (
     seq_freenoteno.nextval
   , #{freenoteTitle}
   , #{freenoteContent}
   , #{freenoteCategory}
   , #{freenoteTopic}
   , #{freenotePrivacy}
   , #{freenotePrivacyComm}
   , #{freenoteCommentYN}
   , #{freenoteWriter}
  )

mapper.update:
update
       freenote
   set freenote_title = #{freenoteTitle}
     , freenote_content = #{freenoteContent}
     , freenote_category = #{freenoteCategory}
     , freenote_topic = #{freenoteTopic}
     , freenote_privacy = #{freenotePrivacy}
     , freenote_privacy_comm = #{freenotePrivacyComm}
     , freenote_comment_yn = #{freenoteCommentYN}
     , modify_date = sysdate
 where freenote_no = #{freenoteNo}

mapper.insert:
insert
  into reply
  (
     reply_no
   , reply_content
   , ref_fno
   , member_no

   , ref_rno

   , reply_level
  )
  values
  (
     seq_replyno.nextval
   , #{replyContent}
   , #{refFno}
   , #{replyWriter}

   , #{refRno}

   , #{replyLevel}
  )

mapper.select:
select count(*)
  from freenote_like
 where member_no = #{freenoteWriter}
   and freenote_no = #{freenoteNo}

mapper.insert:
insert
  into freenote_like
  (
     member_no
   , freenote_no  
  )
  values
  (
     #{freenoteWriter}
   , #{freenoteNo}  
  )

mapper.delete:
delete
  from freenote_like
 where member_no = #{freenoteWriter}
   and freenote_no = #{freenoteNo}

mapper.select:
select count(*)
  from reply_like
 where member_no = #{replyWriter}
   and reply_no = #{replyNo}

mapper.insert:
insert
  into reply_like
  (
     member_no
   , reply_no  
  )
  values
  (
     #{replyWriter}
   , #{replyNo}  
  )

mapper.delete:
delete
  from reply_like
 where member_no = #{replyWriter}
   and reply_no = #{replyNo}

mapper.select:
select
       count(*)
  from report
 where report_writer = #{reportWriter}
   and report_postNo = #{reportPostNo}
   and report_posttype = #{reportPostType}

mapper.insert:
insert 
  into report
  (
     report_no
   , report_writer
   , report_posttype
   , report_type
   , report_content
   , report_link
   , report_postno
  )
  values
  (
     seq_reportno.nextval
   , #{reportWriter}
   , #{reportPostType}
   , #{reportType}
   , #{reportContent}
   , #{reportLink}
   , #{reportPostNo}
  )
